import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';
import AdminNavbar from '../components/AdminNavbar';
import './Admin.css';

const Admin = () => {
  const { user, isAuthenticated, isAdmin, logout } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    users: 0,
    meals: 0,
    orders: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastLogin, setLastLogin] = useState(null);

  // Check if user is admin and redirect if not
  useEffect(() => {
    if (!isAdmin()) {
      navigate('/');
    }
  }, [isAdmin, navigate]);

  // Fetch dashboard statistics
  useEffect(() => {
    const fetchStats = async () => {
      if (!isAdmin()) {
        return; // Don't fetch stats if not admin
      }

      try {
        const token = sessionStorage.getItem('auth_token');
        if (!token) {
          console.error('No authentication token found');
          logout();
          navigate('/login');
          return;
        }

        setLoading(true);
        setError(null);

        // Prepare headers with authorization token
        const headers = {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        };

        // Initialize default values in case any request fails
        let usersCount = 0;
        let mealsCount = 0;
        let ordersCount = 0;

        // Fetch users count
        try {
          const usersResponse = await fetch('http://localhost:3001/users/count', { headers });
          if (usersResponse.ok) {
            const usersData = await usersResponse.json();
            usersCount = usersData.count;
          } else {
            console.error('Error fetching users count:', usersResponse.status);
            // Handle unauthorized access
            if (usersResponse.status === 401 || usersResponse.status === 403) {
              logout();
              navigate('/login');
              return;
            }
          }
        } catch (userError) {
          console.error('Exception fetching users count:', userError);
        }

        // Fetch meals count
        try {
          const mealsResponse = await fetch('http://localhost:3001/meals/count', { headers });
          if (mealsResponse.ok) {
            const mealsData = await mealsResponse.json();
            mealsCount = mealsData.count;
          } else {
            console.error('Error fetching meals count:', mealsResponse.status);
          }
        } catch (mealError) {
          console.error('Exception fetching meals count:', mealError);
        }

        // Fetch orders count
        try {
          const ordersResponse = await fetch('http://localhost:3001/orders/count', { headers });
          if (ordersResponse.ok) {
            const ordersData = await ordersResponse.json();
            ordersCount = ordersData.count;
          } else {
            console.error('Error fetching orders count:', ordersResponse.status);
          }
        } catch (orderError) {
          console.error('Exception fetching orders count:', orderError);
        }

        // Update state with fetched data (using defaults if any request failed)
        setStats({
          users: usersCount,
          meals: mealsCount,
          orders: ordersCount,
          lastLogin: new Date().toLocaleString('he-IL')
        });

        // Set last login time
        setLastLogin(new Date().toLocaleString('he-IL'));
      } catch (err) {
        console.error('Error fetching admin statistics:', err);
        setError(err.message || 'שגיאה בטעינת נתונים מהשרת');

        // Reset stats to zeros
        setStats({
          users: 0,
          meals: 0,
          orders: 0
        });
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [isAdmin, logout, navigate]);

  return (
    <div className="admin-container">
      <AdminNavbar />
      <div className="admin-header">
        <div>
          <h1>שלום {user?.name || 'מנהל'} 👋</h1>
          <p>כאן תוכל לנהל את האתר בקלות</p>
          {lastLogin && <p className="last-login">כניסה אחרונה: {lastLogin}</p>}
        </div>
        <div className="admin-badge">מנהל מערכת</div>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="stats">
        {loading ? (
          <LoadingSpinner message="טוען נתונים..." />
        ) : (
          <>
            <div className="card">
              <h3>🧑‍♂️ משתמשים רשומים</h3>
              <p className="stat-number">{stats.users}</p>
              <Link to="/admin/users" className="stat-action">ניהול משתמשים</Link>
            </div>
            <div className="card">
              <h3>🍱 סך מנות</h3>
              <p className="stat-number">{stats.meals}</p>
              <Link to="/admin/meals" className="stat-action">ניהול מנות</Link>
            </div>
            {/* Commented out orders card
            <div className="card">
              <h3>🛒 הזמנות</h3>
              <p className="stat-number">{stats.orders}</p>
              <Link to="/admin/orders" className="stat-action">צפייה בהזמנות</Link>
            </div>
            */}
          </>
        )}
      </div>

      <div className="admin-sections">
        <div className="admin-section">
          <h2>🍽️ ניהול מנות</h2>
          <div className="admin-actions">
            <Link to="/admin/meals" className="admin-link">
              <span className="icon">🖼️</span>
              ניהול מנות
            </Link>
            <Link to="/admin/add-meal" className="admin-link">
              <span className="icon">➕</span>
              הוספת מנה חדשה
            </Link>
          </div>
        </div>

        <div className="admin-section">
          <h2>👥 ניהול משתמשים</h2>
          <div className="admin-actions">
            <Link to="/admin/users" className="admin-link">
              <span className="icon">👤</span>
              ניהול משתמשים
            </Link>
            {/* Commented out orders link
            <Link to="/admin/orders" className="admin-link">
              <span className="icon">📋</span>
              צפייה בהזמנות
            </Link>
            */}
          </div>
        </div>

        {/* Commented out navigation section
        <div className="admin-section">
          <h2>🔙 ניווט</h2>
          <div className="admin-actions">
            <Link to="/meals" className="admin-link">
              <span className="icon">🍲</span>
              צפייה בתפריט
            </Link>
            <Link to="/" className="admin-link">
              <span className="icon">🏠</span>
              חזרה לדף הבית
            </Link>
          </div>
        </div>
        */}
      </div>
    </div>
  );
};

export default Admin;
