import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './DeleteMeal.css';

const DeleteMeal = () => {
  const { isAdmin, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [confirmMessage, setConfirmMessage] = useState('');
  const [mealToDelete, setMealToDelete] = useState(null);

  // Check if user is admin, redirect if not
  useEffect(() => {
    if (!isAuthenticated() || !isAdmin()) {
      navigate('/');
    }
  }, [isAdmin, isAuthenticated, navigate]);

  // Fetch meals from API
  useEffect(() => {
    const fetchMeals = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:3001/meals');

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setMeals(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching meals:', error);
        setError('◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊î◊û◊†◊ï◊™. ◊ê◊†◊ê ◊†◊°◊î ◊©◊ï◊ë ◊û◊ê◊ï◊ó◊® ◊ô◊ï◊™◊®.');
        setLoading(false);
      }
    };

    fetchMeals();
  }, []);

  // Handle delete click
  const handleDeleteClick = (meal) => {
    setMealToDelete(meal);
    setConfirmMessage(`◊î◊ê◊ù ◊ê◊™◊î ◊ë◊ò◊ï◊ó ◊©◊ë◊®◊¶◊ï◊†◊ö ◊ú◊û◊ó◊ï◊ß ◊ê◊™ "${meal.meal_name}"?`);
  };

  // Handle delete confirmation
  const confirmDelete = async () => {
    if (mealToDelete) {
      try {
        // Check if user is admin before submitting
        if (!isAdmin()) {
          setConfirmMessage('◊ê◊ô◊ü ◊ú◊ö ◊î◊®◊©◊ê◊ï◊™ ◊û◊†◊î◊ú ◊ú◊ë◊ô◊¶◊ï◊¢ ◊§◊¢◊ï◊ú◊î ◊ñ◊ï');
          return;
        }

        // Get authentication token from session storage
        const token = sessionStorage.getItem('auth_token');

        if (!token) {
          setError('◊†◊ì◊®◊©◊™ ◊î◊™◊ó◊ë◊®◊ï◊™ ◊û◊ó◊ì◊©');
          navigate('/login');
          return;
        }

        // Delete the meal from the server with authentication token
        const response = await fetch(`http://localhost:3001/meals/${mealToDelete.meal_id}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        let data;
        try {
          data = await response.json();
        } catch (e) {
          console.error('Error parsing response:', e);
          // If we can't parse JSON, it might be that the server returned empty response on success
          if (response.ok) {
            // Update the meals list on success
            setMeals(meals.filter(meal => meal.meal_id !== mealToDelete.meal_id));
            setSuccess(`◊î◊û◊†◊î ${mealToDelete.meal_name} ◊†◊û◊ó◊ß◊î ◊ë◊î◊¶◊ú◊ó◊î`);
            setMealToDelete(null);
            return;
          }
          throw new Error(`◊©◊í◊ô◊ê◊î ◊ë◊§◊¢◊†◊ï◊ó ◊™◊©◊ï◊ë◊™ ◊î◊©◊®◊™: ${e.message}`);
        }

        if (!response.ok) {
          console.error('Server error response:', data);

          if (response.status === 403) {
            throw new Error('◊ê◊ô◊ü ◊ú◊ö ◊î◊®◊©◊ê◊ï◊™ ◊û◊†◊î◊ú ◊ú◊ë◊ô◊¶◊ï◊¢ ◊§◊¢◊ï◊ú◊î ◊ñ◊ï');
          } else if (response.status === 401) {
            throw new Error('◊†◊ì◊®◊©◊™ ◊î◊™◊ó◊ë◊®◊ï◊™ ◊û◊ó◊ì◊©');
          } else if (response.status === 404) {
            throw new Error(`◊î◊û◊†◊î ◊ú◊ê ◊†◊û◊¶◊ê◊î ◊ë◊û◊¢◊®◊õ◊™`);
          } else {
            throw new Error(`◊©◊í◊ô◊ê◊î ◊ë◊û◊ó◊ô◊ß◊™ ◊î◊û◊†◊î: ${data.error || `◊ß◊ï◊ì ◊©◊í◊ô◊ê◊î: ${response.status}`}`);
          }
        }

        // Update the meals list
        setMeals(meals.filter(meal => meal.meal_id !== mealToDelete.meal_id));
        setSuccess(`◊î◊û◊†◊î ${mealToDelete.meal_name} ◊†◊û◊ó◊ß◊î ◊ë◊î◊¶◊ú◊ó◊î`);

        // Clear the confirmation message
        setConfirmMessage('');
        setMealToDelete(null);
      } catch (error) {
        console.error('Error deleting meal:', error);
        setConfirmMessage('◊©◊í◊ô◊ê◊î ◊ë◊û◊ó◊ô◊ß◊™ ◊î◊û◊†◊î. ◊ê◊†◊ê ◊†◊°◊î ◊©◊ï◊ë.');
      }
    }
  };

  // Cancel delete
  const cancelDelete = () => {
    setConfirmMessage('');
    setMealToDelete(null);
  };

  return (
    <div className="delete-meal-container">
      <header>
        <h1>üóëÔ∏è ◊û◊ó◊ô◊ß◊™ ◊û◊†◊ï◊™</h1>
        <nav>
          <Link to="/admin">◊ì◊£ ◊†◊ô◊î◊ï◊ú</Link>
          <Link to="/orders">◊î◊ñ◊û◊†◊ï◊™</Link>
          <Link to="/admin/users">◊†◊ô◊î◊ï◊ú ◊û◊©◊™◊û◊©◊ô◊ù</Link>
          <Link to="/admin/add-meal">◊î◊ï◊°◊§◊™ ◊û◊†◊î</Link>
        </nav>
      </header>

      <main>
        {loading ? (
          <div className="loading">◊ò◊ï◊¢◊ü ◊û◊†◊ï◊™...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : (
          <>
            {confirmMessage && (
              <div className="confirm-dialog">
                <p>{confirmMessage}</p>
                <div className="confirm-actions">
                  <button onClick={confirmDelete} className="confirm-btn">◊õ◊ü, ◊û◊ó◊ß</button>
                  <button onClick={cancelDelete} className="cancel-btn">◊ë◊ô◊ò◊ï◊ú</button>
                </div>
              </div>
            )}

            {meals.length === 0 ? (
              <div className="no-meals">◊ú◊ê ◊†◊û◊¶◊ê◊ï ◊û◊†◊ï◊™</div>
            ) : (
              <table>
                <thead>
