// src/pages/AdminOrders.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Admin.css";
import { Link } from "react-router-dom";

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [error, setError] = useState(null);

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:3001/orders/all");
      setOrders(res.data);
      setFilteredOrders(res.data);
    } catch (err) {
      setError("שגיאה בטעינת ההזמנות");
      console.error(err);
    }
  };

  // Apply all filters (search and status)
  const applyFilters = () => {
    let filtered = [...orders];

    // Apply search term filter if exists
    if (searchTerm.trim()) {
      filtered = filtered.filter(order => {
        const orderIdMatch = order.order_id?.toString().includes(searchTerm);
        const nameMatch = `${order.first_name || ''} ${order.last_name || ''}`.toLowerCase().includes(searchTerm.toLowerCase());
        return orderIdMatch || nameMatch;
      });
    }

    // Apply status filter if not 'all'
    if (statusFilter !== 'all') {
      filtered = filtered.filter(order => order.status === statusFilter);
    }

    setFilteredOrders(filtered);
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    // We'll apply filters after setting the search term
    setTimeout(() => applyFilters(), 0);
  };

  // Handle status filter change
  const handleStatusFilterChange = (e) => {
    setStatusFilter(e.target.value);
    // We'll apply filters after setting the status filter
    setTimeout(() => applyFilters(), 0);
  };

  // Update status
  const handleStatusChange = async (orderId, newStatus) => {
    try {
      await axios.patch(`http://localhost:3001/orders/${orderId}/status`, { status: newStatus });
      setOrders(prev => prev.map(o => o.order_id === orderId ? { ...o, status: newStatus } : o));
    } catch (err) {
      console.error(err);
      alert('שגיאה בעדכון הסטטוס');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  // Re-apply filters when orders change
  useEffect(() => {
    applyFilters();
  }, [orders]);

  return (
    <div className="admin-container">
      <h1 className="main-header">📦 הזמנות מהלקוחות</h1>
      {error && <div className="error-message">{error}</div>}

      <div className="admin-section">
        <h2>רשימת הזמנות</h2>

        {/* Filter controls */}
        <div className="filters-container" style={{ margin: "1rem 0", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          {/* Search input */}
          <div className="search-container">
            <input
              type="text"
              placeholder="חיפוש לפי שם משתמש או מספר הזמנה"
              value={searchTerm}
              onChange={handleSearchChange}
              style={{
                padding: "0.5rem",
                width: "300px",
                borderRadius: "4px",
                border: "1px solid #ccc",
                direction: "rtl"
              }}
            />
          </div>

          {/* Status filter */}
          <div className="status-filter-container" style={{ textAlign: "right" }}>
            <label htmlFor="status-filter" style={{ marginLeft: "0.5rem" }}>סינון לפי סטטוס: </label>
            <select 
              id="status-filter"
              value={statusFilter}
              onChange={handleStatusFilterChange}
              style={{
                padding: "0.5rem",
                borderRadius: "4px",
                border: "1px solid #ccc"
              }}
            >
              <option value="all">הכל</option>
              <option value="pending">pending</option>
              <option value="processing">processing</option>
              <option value="delivered">delivered</option>
              <option value="canceled">canceled</option>
            </select>
          </div>
        </div>
        {orders.length === 0 ? (
          <p>אין הזמנות להצגה.</p>
        ) : filteredOrders.length === 0 ? (
          <p>לא נמצאו הזמנות התואמות לחיפוש.</p>
        ) : (
          <table className="orders-table">
            <thead>
              <tr>
                <th>מספר הזמנה</th>
                <th>תאריך</th>
                <th>שם משתמש</th>
                <th>פריטים</th>
                <th>סטטוס</th>
              </tr>
            </thead>
            <tbody>
              {filteredOrders.map((order) => (
                <tr key={order.order_id}>
                  <td>{order.order_id}</td>
                  <td>{order.order_date ? new Date(order.order_date).toLocaleDateString('he-IL') : '—'}</td>
                  <td>{`${order.first_name || ''} ${order.last_name || ''}`.trim() || '—'}</td>
                  <td>{order.items}</td>
                  <td>
                    <select value={order.status} onChange={e => handleStatusChange(order.order_id, e.target.value)}>
                      <option value="pending">pending</option>
                      <option value="processing">processing</option>
                      <option value="delivered">delivered</option>
                      <option value="canceled">canceled</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <div style={{ marginTop: "2rem" }}>
        <Link to="/admin" className="admin-link">⬅ חזרה לניהול</Link>
      </div>
    </div>
  );
};

export default AdminOrders;
