// src/pages/AdminOrders.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Admin.css";
import { Link } from "react-router-dom";

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [error, setError] = useState(null);

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:3001/orders/all");
      setOrders(res.data);
      setFilteredOrders(res.data);
    } catch (err) {
      setError("◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊î◊î◊ñ◊û◊†◊ï◊™");
      console.error(err);
    }
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (!value.trim()) {
      // If search is empty, show all orders
      setFilteredOrders(orders);
      return;
    }

    // Filter orders by order_id or user name
    const filtered = orders.filter(order => {
      const orderIdMatch = order.order_id?.toString().includes(value);
      const nameMatch = `${order.first_name || ''} ${order.last_name || ''}`.toLowerCase().includes(value.toLowerCase());
      return orderIdMatch || nameMatch;
    });

    setFilteredOrders(filtered);
  };

  // Update status
  const handleStatusChange = async (orderId, newStatus) => {
    try {
      await axios.patch(`http://localhost:3001/orders/${orderId}/status`, { status: newStatus });
      setOrders(prev => prev.map(o => o.order_id === orderId ? { ...o, status: newStatus } : o));
    } catch (err) {
      console.error(err);
      alert('◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü ◊î◊°◊ò◊ò◊ï◊°');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div className="admin-container">
      <h1 className="main-header">üì¶ ◊î◊ñ◊û◊†◊ï◊™ ◊û◊î◊ú◊ß◊ï◊ó◊ï◊™</h1>
      {error && <div className="error-message">{error}</div>}

      <div className="admin-section">
        <h2>◊®◊©◊ô◊û◊™ ◊î◊ñ◊û◊†◊ï◊™</h2>

        {/* Search input */}
        <div className="search-container" style={{ margin: "1rem 0", textAlign: "right" }}>
          <input
            type="text"
            placeholder="◊ó◊ô◊§◊ï◊© ◊ú◊§◊ô ◊©◊ù ◊û◊©◊™◊û◊© ◊ê◊ï ◊û◊°◊§◊® ◊î◊ñ◊û◊†◊î"
            value={searchTerm}
            onChange={handleSearchChange}
            style={{
              padding: "0.5rem",
              width: "300px",
              borderRadius: "4px",
              border: "1px solid #ccc",
              direction: "rtl"
            }}
          />
        </div>
        {orders.length === 0 ? (
          <p>◊ê◊ô◊ü ◊î◊ñ◊û◊†◊ï◊™ ◊ú◊î◊¶◊í◊î.</p>
        ) : filteredOrders.length === 0 ? (
          <p>◊ú◊ê ◊†◊û◊¶◊ê◊ï ◊î◊ñ◊û◊†◊ï◊™ ◊î◊™◊ï◊ê◊û◊ï◊™ ◊ú◊ó◊ô◊§◊ï◊©.</p>
        ) : (
          <table className="orders-table">
            <thead>
              <tr>
                <th>◊û◊°◊§◊® ◊î◊ñ◊û◊†◊î</th>
                <th>◊™◊ê◊®◊ô◊ö</th>
                <th>◊©◊ù ◊û◊©◊™◊û◊©</th>
                <th>◊§◊®◊ô◊ò◊ô◊ù</th>
                <th>◊°◊ò◊ò◊ï◊°</th>
              </tr>
            </thead>
            <tbody>
              {filteredOrders.map((order) => (
                <tr key={order.order_id}>
                  <td>{order.order_id}</td>
                  <td>{order.order_date ? new Date(order.order_date).toLocaleDateString('he-IL') : '‚Äî'}</td>
                  <td>{`${order.first_name || ''} ${order.last_name || ''}`.trim() || '‚Äî'}</td>
                  <td>{order.items}</td>
                  <td>
                    <select value={order.status} onChange={e => handleStatusChange(order.order_id, e.target.value)}>
                      <option value="pending">pending</option>
                      <option value="processing">processing</option>
                      <option value="delivered">delivered</option>
                      <option value="canceled">canceled</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <div style={{ marginTop: "2rem" }}>
        <Link to="/admin" className="admin-link">‚¨Ö ◊ó◊ñ◊®◊î ◊ú◊†◊ô◊î◊ï◊ú</Link>
      </div>
    </div>
  );
};

export default AdminOrders;
