import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useCart } from '../contexts/CartContext';
import { mealApi } from '../services/api';
import './Cart.css';

const Cart = () => {
  const [message, setMessage] = useState('');
  const { isAuthenticated } = useAuth();
  const { cart, updateQty, removeItem, clearCart, totalItems, totalPrice } = useCart();
  const navigate = useNavigate();

  // Check if user is authenticated
  if (!isAuthenticated()) {
    navigate('/login', { state: { from: '/cart' } });
  }

  const handleRemoveFromCart = (itemId) => {
    removeItem(itemId);
  };

  const handleUpdateQuantity = (itemId, newQuantity) => {
    if (newQuantity < 1) {
      removeItem(itemId);
      return;
    }

    updateQty(itemId, newQuantity);
  };

  const getTotalPrice = () => {
    return totalPrice;
  };

  const checkout = async () => {
    if (cart.length === 0) {
      setMessage('住 砖 专拽');
      return;
    }

    try {
      // Create orders for each item in cart
      for (const item of cart) {
        await mealApi.createOrder({
          meal_id: item.id,
          quantity: item.qty,
          status: 'pending'
        });
      }

      // Clear cart and show success message
      clearCart();
      setMessage(' 爪注 爪!');

      // Clear message after 3 seconds
      setTimeout(() => {
        setMessage('');
      }, 3000);
    } catch (error) {
      setMessage('砖 爪注 .  住 砖.');
      console.error('Checkout error:', error);
    }
  };

  return (
    <div className="cart-page">
      <header>
        <h1> 住 拽转 砖</h1>
        <nav>
          <Link to="/home">祝 转</Link>
          <Link to="/meals">转驻专</Link>
          <Link to="/cart">住 拽转</Link>
          <Link to="/contact">爪专 拽砖专</Link>
          {!isAuthenticated() && (
            <>
              <Link to="/login">转专转</Link>
              <Link to="/signup">专砖</Link>
            </>
          )}
          {isAuthenticated() && (
            <Link to="#" onClick={() => {
              // Add logout functionality here
              // For now, just redirect to home
              navigate('/home');
            }}>转转拽</Link>
          )}
        </nav>
      </header>

      {message && (
        <div className="message">
          {message}
        </div>
      )}

      <main className="cart-container">
        <h2>驻专 住 拽转</h2>

        {cart.length === 0 ? (
          <div className="empty-cart">
            <p>住 砖 专拽</p>
            <Link to="/meals" className="continue-shopping">砖 拽转</Link>
          </div>
        ) : (
          <>
            <div className="cart-items">
              <table className="cart-table">
                <thead>
                  <tr>
                    <th></th>
                    <th>专</th>
                    <th>转</th>
                    <th>住"</th>
                    <th>驻注转</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map(item => (
                    <tr key={item.id}>
                      <td className="meal-info">
                        <img 
                          src={item.imgUrl || `${process.env.PUBLIC_URL}/images/default-meal.png`} 
                          alt={item.name} 
                        />
                        <div>
                          <h3>{item.name}</h3>
                          <p>{item.description}</p>
                        </div>
                      </td>
                      <td className="price">{item.price}</td>
                      <td className="quantity">
                        <div className="quantity-controls">
                          <button onClick={() => handleUpdateQuantity(item.id, item.qty - 1)}>-</button>
                          <span>{item.qty}</span>
                          <button onClick={() => handleUpdateQuantity(item.id, item.qty + 1)}>+</button>
                        </div>
                      </td>
                      <td className="total">{item.price * item.qty}</td>
                      <td className="actions">
                        <button 
                          className="remove-button" 
                          onClick={() => handleRemoveFromCart(item.id)}
                        >
                          住专
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div className="cart-summary">
              <div className="summary-row">
                <span>住" 驻专:</span>
                <span>{totalItems}</span>
              </div>
              <div className="summary-row">
                <span>住" 转砖:</span>
                <span className="total-price">{totalPrice}</span>
              </div>
              <button 
                className="checkout-button" 
                onClick={checkout}
              >
                转砖
              </button>
              <Link to="/meals" className="continue-shopping">砖 拽转</Link>
            </div>
          </>
        )}
      </main>

      <footer>
        <p>漏 2025 专  专 -  转 砖专转</p>
      </footer>
    </div>
  );
};

export default Cart;
