import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './UpdateUser.css';

const UpdateUser = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const userId = queryParams.get('id');

  // Initial form state
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    age: '',
    status: 'פעיל'
  });

  const [message, setMessage] = useState('');

  // In a real app, you would fetch the user details from an API
  useEffect(() => {
    // Mock data - simulating API fetch based on userId
    if (userId === '1') {
      setFormData({
        firstName: 'דנה',
        lastName: 'כהן',
        email: 'dana@email.com',
        phone: '050-1234567',
        age: '28',
        status: 'פעיל'
      });
    } else if (userId === '2') {
      setFormData({
        firstName: 'רועי',
        lastName: 'לוי',
        email: 'roi@email.com',
        phone: '052-7654321',
        age: '34',
        status: 'פעיל'
      });
    }
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // In a real app, you would make an API call to update the user
    console.log('User data to be updated:', { userId, ...formData });

    // Show success message
    setMessage('פרטי המשתמש עודכנו בהצלחה!');

    // Clear message after 2 seconds and redirect
    setTimeout(() => {
      setMessage('');
      navigate('/admin/users');
    }, 2000);
  };

  return (
    <div className="update-user-container">
      <header>
        <h1>📝 עדכון פרטי משתמש</h1>
        <nav>
          <Link to="/admin">דף ניהול</Link>
          <Link to="/admin/users">רשימת משתמשים</Link>
          <Link to="/orders">הזמנות</Link>
          <Link to="/">חזרה לאתר</Link>
        </nav>
      </header>

      <main>
        {message && <div className="success-message">{message}</div>}

        <form className="update-user-form" onSubmit={handleSubmit}>
          <label>
            שם פרטי:
            <input 
              type="text" 
              name="firstName" 
              value={formData.firstName}
              onChange={handleChange}
              required 
            />
          </label>

          <label>
            שם משפחה:
            <input 
              type="text" 
              name="lastName" 
              value={formData.lastName}
              onChange={handleChange}
              required 
            />
          </label>

          <label>
            אימייל:
            <input 
              type="email" 
              name="email" 
              value={formData.email}
              onChange={handleChange}
              required 
            />
          </label>

          <label>
            טלפון:
            <input 
              type="tel" 
              name="phone" 
              value={formData.phone}
              onChange={handleChange}
              required 
            />
          </label>

          <label>
            גיל:
            <input 
              type="number" 
              name="age" 
              value={formData.age}
              onChange={handleChange}
              required 
            />
          </label>

          <label>
            סטטוס:
            <select 
              name="status"
              value={formData.status}
              onChange={handleChange}
            >
              <option value="פעיל">פעיל</option>
              <option value="מוקפא">מוקפא</option>
            </select>
          </label>

          <div className="form-actions">
            <button type="submit">💾 שמור שינויים</button>
            <Link to="/admin/users" className="back-btn">⬅️ חזרה לרשימת המשתמשים</Link>
          </div>
        </form>
      </main>

      <footer>
        <p>דף עדכון משתמש</p>
      </footer>
    </div>
  );
};

export default UpdateUser;
