import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './Header.css';

const Header = () => {
  const { isAuthenticated, isAdmin, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <header className="main-header">
      <div className="logo"></div>
      <nav className="nav">
        <Link to="/">דף הבית</Link>
        {!isAuthenticated() && <Link to="/signup">הרשמה</Link>}
        {!isAuthenticated() && <Link to="/login">התחברות</Link>}
        <Link to="/meals">קניית מנות</Link>
        <Link to="/contact">צור קשר</Link>
        {isAuthenticated() && <Link to="/cart">עגלת קניות</Link>}
        {isAdmin() && <Link to="/admin" className="admin-link">ניהול</Link>}
        {isAuthenticated() && <button onClick={handleLogout} className="logout-btn">התנתק</button>}
      </nav>
    </header>
  );
};

export default Header;
